sys_script_include.script:
var Lookup = Class.create();
Lookup.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	initialize: function() {

	},
	Action:function(){
		var queueAction = '';
		var queueCurrent = current.queue_flow;
		if(!queueCurrent)
			return;
		var queuerecord = new GlideRecord ('x_ananc_alight4sn_lookup');
		queuerecord.addQuery('action','=','queue');
		queuerecord.query();

		while(queuerecord.next()){
			if (queueAction.length > 0) {
				queueAction+=(','+queuerecord.sys_id).toString();
			}
			else{
				queueAction = ''+queuerecord.sys_id.toString();
			}
		}
		return 'sys_idIN'+queueAction;
	},

    type: 'Lookup'
});
