sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
		var body = request.body;
    var urlParams = request.queryParams;

	var name = urlParams["name"];
	if (name != null) {
		name = name[0];
	}

	var timezone = urlParams["timezone"];
	if (timezone != null) {
		timezone = timezone[0];
	}

	var datestr = urlParams["date"];
	if (datestr != null) {
		datestr = datestr[0];
	}

	var date;
	if (datestr != null) {
		date = new GlideDateTime(datestr);
	} else {
		date = new GlideDateTime();
	}

	var data = {
		schedule: null,
		date: datestr,
		glideDate: date.getDate().getByFormat("EEE yyyy-MM-dd hh:mm:ss Z"),
		tzoffset: date.getTZOffset()/3600000,
		dst: date.isDST(),
		name: name
	};

	var g = new GlideRecord('cmn_schedule');
	g.addQuery("sys_name", name);
	g.query();
	if (g.next()) {
	   data.schedule = {
		   id: g.getUniqueValue(),
		   recordClass: g.getRecordClassName(),
		   classDisplayValue: g.getClassDisplayValue(),
		   displayValue: g.getDisplayValue(),
		   query: g.getEncodedQuery(),
		   label: g.getLabel(),
		   link: g.getLink(),
	   };

		var schedule = new GlideSchedule(g.sys_id);

		if (timezone != null) {
			data.in_schedule = schedule.isInSchedule (date, timezone);
		} else {
			data.in_schedule = schedule.isInSchedule (date);
		}
	}
	return data;
})(request, response);
